import { useDispatch, useSelector } from 'react-redux';
import { ACTIONS } from '../../store';
import classes from './Counter.module.css';
import { useState } from 'react';
import CounterHeader from './CounterHeader';

const Counter = () => {
  // useSelector allows us to extract given part of the Redux store state
  // automatically subscribes to the Redux store and runs the provided selector function whenever an action is dispatched
  const counter = useSelector((state) => state.counter);
  const dispatch = useDispatch();
  const [value, setValue] = useState(5);
  const toggleCounterHandler = () => { };
  console.log('RENDER: Counter');
  return (
    <main className={classes.counter}>
      <CounterHeader />
      <div className={classes.value}>{counter}</div>
      <div style={{ marginBottom: '1rem' }}>
        <label htmlFor="value">Set Increment/Decrement Value: </label>
        <input
          type="number"
          id="value"
          value={value}
          onChange={(e) => setValue(+e.target.value)}
        />
      </div>
      <div style={{ display: 'flex', gap: '1rem', justifyContent: 'center', marginBottom: '1rem' }}>
        <button onClick={() => dispatch(ACTIONS.INC)}>Increment</button>
        <button onClick={() => dispatch({ ...ACTIONS.CHANGE, payload: value })}>Change by value</button>
        <button onClick={() => dispatch(ACTIONS.DEC)}>Decrement</button>
      </div>
      <button onClick={toggleCounterHandler}>Toggle Counter</button>
    </main>
  );
};

export default Counter;
