import { createStore } from 'redux';

const INITIAL_STATE = {
  counter: 0,
  showCounter: true,
};

const ACTION_TYPES = {
  INC: 'inc',
  DEC: 'dec',
  CHANGE: 'change',
  TOGGLE: 'toggle',
};

const counterReducer = (state, action) => {
  switch (action.type) {
    case ACTION_TYPES.INC:
      return { ...state, counter: state.counter + 1 }
    case ACTION_TYPES.DEC:
      return { ...state, counter: state.counter - 1 }
    case ACTION_TYPES.CHANGE:
      return { ...state, counter: state.counter + action.payload }
    case ACTION_TYPES.TOGGLE:
      return { ...state, showCounter: !state.showCounter }
    default:
      return state;
  }
}

const store = createStore(counterReducer, INITIAL_STATE);

export default store;

export const ACTIONS = Object.keys(ACTION_TYPES).reduce((acc, key) => {
  acc[key.toUpperCase()] = { type: ACTION_TYPES[key] };
  return acc;
}, {});

console.log(ACTIONS);
